function horizontalBuffers(table) {
    const length = table.length;
    table.forEach((element) => {
        element.push("O");
        element.unshift("O");
    });
}

function verticalBuffers(table) {
    const row = [];
    for (let index = 0; index < table[0].length; index += 1) {
        row.push("O");
    }
    table.unshift(row);
    table.push(row);
}
