function canMove(table, cIndex, rIndex, direction) {
    if (direction === "right") {
        if (table[cIndex][rIndex + 1] === "O") {
            active = false;
            return false;
        }
        if (table[cIndex][rIndex + 1] === "#") {
            return false;
        }
    }
    if (direction === "left") {
        if (table[cIndex][rIndex - 1] === "O") {
            active = false;
            return false;
        }
        if (table[cIndex][rIndex - 1] === "#") {
            return false;
        }
    }
    if (direction === "up") {
        // console.log(table[cIndex - 1][rIndex] === "O");
        if (table[cIndex - 1][rIndex] === "O") {
            active = false;
            return false;
        }
        if (table[cIndex - 1][rIndex] === "#") {
            return false;
        }
    }
    if (direction === "down") {
        if (table[cIndex + 1][rIndex] === "O") {
            active = false;
            return false;
        }
        if (table[cIndex + 1][rIndex] === "#") {
            return false;
        }
    }
    return true;
}

function newCoordinates(cIndex, rIndex, direction) {
    if (direction === "right") {
        return [0, 1];
    }
    if (direction === "left") {
        return [0, -1];
    }
    if (direction === "up") {
        return [-1, 0];
    }
    if (direction === "down") {
        return [1, 0];
    }
    return true;
}

